public class Problem008 {

	public static void main(String[] args) {

		String number="";
		char [] chars = number.toCharArray();
		long max=0l;
		for(int x=0; x<988; x++){
			long a1=Integer.valueOf(chars[x])-48;//We checked ascii table for that.
			long a2=Integer.valueOf(chars[x+1])-48;
			long a3=Integer.valueOf(chars[x+2])-48;
			long a4=Integer.valueOf(chars[x+3])-48;
			long a5=Integer.valueOf(chars[x+4])-48;
			long a6=Integer.valueOf(chars[x+5])-48;
			long a7=Integer.valueOf(chars[x+6])-48;
			long a8=Integer.valueOf(chars[x+7])-48;
			long a9=Integer.valueOf(chars[x+8])-48;
			long a10=Integer.valueOf(chars[x+9])-48;
			long a11=Integer.valueOf(chars[x+10])-48;
			long a12=Integer.valueOf(chars[x+11])-48;
			long a13=Integer.valueOf(chars[x+12])-48;
			
			
			
			if((a1*a2*a3*a4*a5*a6*a7*a8*a9*a10*a11*a12*a13)>=max)
			{
				max=(a1*a2*a3*a4*a5*a6*a7*a8*a9*a10*a11*a12*a13);				
			}
		}
		System.out.println(max);
	}

}
